mapper.select:
SELECT a.DATA_ID AS dataId , a.OWNER_ID AS ownerId , b.IND_USER_BIRTH_DT AS indUserBirthDt , b.IND_USER_RACE_CD AS indUserRaceCd , a.DATA_ACQ_DT AS dataAcqDt , ( SELECT C.COMM_CODE_NM FROM BCK_COMM_CODE_M C WHERE C.COMM_CODE = b.IND_USER_RACE_CD AND C.CATE_CODE = 'ETHNY' ) AS indUserRaceCdNm , b.IND_USER_GENDER AS indUserGender , a.DATA_TYPE_CD AS dataTypeCd , ( SELECT C.COMM_CODE_NM FROM BCK_COMM_CODE_M C WHERE C.COMM_CODE = a.DATA_TYPE_CD AND C.CATE_CODE = 'PMTSS' ) AS dataTypeCdNm , a.DATA_CATE_CD AS dataCateCd , ( SELECT C.COMM_CODE_NM FROM BCK_COMM_CODE_M C WHERE C.COMM_CODE = a.DATA_CATE_CD AND C.CATE_CODE = 'BDCTGR' ) AS dataCateCdNm , a.DATA_FRMT1_CD AS dataFrmt1Cd , ( SELECT C.COMM_CODE_NM FROM BCK_COMM_CODE_M C WHERE C.COMM_CODE = a.DATA_FRMT1_CD AND C.CATE_CODE = 'FRMT1' ) AS dataFrmt1CdNm , a.DATA_ACQ_SITE_CD AS dataAcqSiteCd , ( SELECT C.COMM_CODE_NM FROM BCK_COMM_CODE_M C WHERE C.COMM_CODE = a.DATA_ACQ_SITE_CD AND C.CATE_CODE = 'HSPTL' ) AS dataAcqSiteCdNm , a.DATA_RFS_YN AS dataRfsYn , a.DATA_DMFS_YN AS dataDmfsYn , a.DATA_ESTGN_YN AS dataEstgnYn , a.DATA_PRGTN_YN AS dataPrgtnYn , a.DATA_HER2_YN AS dataHer2Yn , a.DATA_MNP_YN AS dataMnpYn , a.DATA_FMLY_YN AS dataFmlyYn , a.DATA_TMR_SZ AS dataTmrSz , a.DATA_HST_GR AS dataHstGr , a.DATA_PN_STG AS dataPnStg , date_part('year' , age(to_date(to_char(current_date , 'YYYYMMDD'), 'YYYYMMDD') , to_date(b.ind_user_birth_dt, 'YYYYMMDD') ) ) AS indUserAge FROM BCK_BIO_DATA_M a , BCK_IND_USER_M b WHERE a.OWNER_ID = b.IND_USER_ID AND a.DEL_YN = 'N' AND a.DATA_TYPE_CD = #{dataTypeCd}
AND a.DATA_FRMT1_CD = #{dataFrmt1Cd}
AND a.DATA_ID IN
#{dataId}
